import React, { useState } from 'react'
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import './Navbar.css'

export default function Navbar() {
  const [showNav, setShowNav] = useState(false)

  return (
      <nav className='nav'>
        <Link to='/'><h1 className='nav__logo'>SANAT</h1></Link>
        <button className='nav__burger' onClick={() => setShowNav(!showNav)}>
          {showNav ? 'Close' : 'Open'}
        </button>
        <ul className={`nav__items ${showNav ? 'nav__items--open' : ''}`}>
          <Link to='/'><li className='nav__item'>Home</li></Link>
          <Link to='/content'><li className='nav__item'>Content</li></Link>
          <Link to='/contact'><li className='nav__item'>Contact</li></Link>
        </ul>
      </nav>
  )
}
