import React, { useState, useEffect } from 'react';
import './Header.css'
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { useNavigate } from 'react-router-dom';



function Header(props) {
  const navigate = useNavigate();

  const handleClick = () => {
    navigate('/mayuan');
  };

  return (
    <header className='header'>
      <div className='image__container'>
        <img src={props.image} className='header__image' />
        <div className='image__text' style={{backgroundColor: props.isWhite ? 'rgba(0, 0, 0, .7)' : 'rgba(0, 0, 0, .3)'}}>
          <h2 className='image__title'>{props.title}</h2>
          <h3 className='image__subtitle'>{props.subtitle}</h3>
          <button onClick={handleClick} className='image__button'>Learn more</button>
        </div>
      </div>
    </header>
  );
}



export default Header;
