import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Header.css'
import maYuanImage from '../assets/ma-yuan.jpg';
import breigelImage from '../assets/breigel.jpg';
import assaImage from '../assets/assa.png';

function Header() {
  const [slideIndex, setSlideIndex] = useState(0);
  const [slides, setSlides] = useState([
    {
      title: 'Slide 1',
      subtitle: 'Subtitle 1',
      image: maYuanImage,
      link: '/slide1',
      isWhite: true
    },
    {
      title: 'Slide 2',
      subtitle: 'Subtitle 2',
      image: breigelImage,
      link: '/slide2',
      isWhite: false
    },
    {
      title: 'Slide 3',
      subtitle: 'Subtitle 3',
      image: assaImage,
      link: '/slide3',
      isWhite: true
    }
  ]);

  function handlePreviousSlide() {
    if (slideIndex > 0) {
      setSlideIndex(slideIndex - 1);
    }
  }

  function handleNextSlide() {
    if (slideIndex < slides.length - 1) {
      setSlideIndex(slideIndex + 1);
    }
  }

  return (
    <header className='header'>
      <div className='header__slides'>
        {slides.map((slide, index) => (
          <div
            key={index}
            className={`header__slide ${index === slideIndex ? 'header__slide--active' : ''}`}
            style={{ display: index === slideIndex ? 'block' : 'none' }}
          >
            <div className='image__container'>
              <img src={slide.image} className='header__image' />
              <div
                className='image__text'
                style={{ backgroundColor: slide.isWhite ? 'rgba(0, 0, 0, .7)' : 'rgba(0, 0, 0, .3)' }}
              >
                <h2 className='image__title'>{slide.title}</h2>
                <h3 className='image__subtitle'>{slide.subtitle}</h3>
                <Link to={slide.link}>
                  <button className='image__button'>Learn more</button>
                </Link>
              </div>
            </div>
          </div>
        ))}
      </div>
      <div className='header__controls'>
        <button className='header__control header__control--previous' onClick={handlePreviousSlide}>
          &lt;
        </button>
        <button className='header__control header__control--next' onClick={handleNextSlide}>
          &gt;
        </button>
      </div>
    </header>
  );
}
