import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './Header.css'
import data from '../assets/data';

function Header(props) {
  const [articleId, setArticleId] = useState(null);

  const handleButtonClick = (id) => {
    setArticleId(id);
  };

  return (
    <header className='header'>
      <div className='image__container'>
        <img src={props.image} className='header__image' />
        <div className='image__text' style={{backgroundColor: props.isWhite ? 'rgba(0, 0, 0, .7)' : 'rgba(0, 0, 0, .3)'}}>
          <h2 className='image__title'>{props.title}</h2>
          <h3 className='image__subtitle'>{props.subtitle}</h3>
          <button
            className='image__button'
            onClick={() => handleButtonClick(props.id)}
          >
            Learn more
          </button>
        </div>
      </div>
      {articleId === data[0].id && <Link to='/mayuan' />}
    </header>
  );
}

export default Header;
