import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Header.css'

function Header(props) {
  const [selectedArticle, setSelectedArticle] = useState(null);

  const handleClick = (article) => {
    setSelectedArticle(article);
  }

  return (
    <header className='header'>
      <div className='image__container'>
       <img src={props.image} className='header__image' />
        <div className='image__text' style={{backgroundColor: props.isWhite ? 'rgba(0, 0, 0, .7)' : 'rgba(0, 0, 0, .3)'}}>
          <h2 className='image__title'>{props.title}</h2>
          <h3 className='image__subtitle'>{props.subtitle}</h3>
          <Link to={{ pathname: "/article", state: { selectedArticle } }}><button className='image__button' onClick={() => handleClick(props)}>Learn more</button></Link>
        </div>
      </div>
    </header>
  );
}

export default Header;
