import React, { useState, useEffect } from 'react';
import './Header.css'

function Header(props) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const images = props.images;

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((currentIndex + 1) % images.length);
    }, 3000);
    return () => clearInterval(intervalId);
  }, [setCurrentIndex, images.length]);

  return (
    <header className='header'>
      <div className='image__container'>
        <img src={images[currentIndex].image} className='header__image' />
        <div className='image__text' style={{backgroundColor: props.isWhite ? 'rgba(0, 0, 0, .7)' : 'rgba(0, 0, 0, .3)'}}>
          <h2 className='image__title'>{images[currentIndex].title}</h2>
          <h3 className='image__subtitle'>{images[currentIndex].subtitle}</h3>
          <button className='image__button'>Learn more</button>
        </div>
      </div>
    </header>
  );
}

export default Header;
