import React from 'react';
import './Header.css';

function Header(props) {
  return (
    <header className="header">
      <div className="image__container">
        <img src={props.image} className="header__image" />
        <div
          className="image__text"
          style={{
            backgroundColor: props.isWhite
              ? 'rgba(0, 0, 0, .7)'
              : 'rgba(0, 0, 0, .3)'
          }}
        >
          <h2 className="image__title">{props.title}</h2>
          <h3 className="image__subtitle">{props.subtitle}</h3>
          <a href={props.link}>
            <button className="image__button">Learn more</button>
          </a>
        </div>
      </div>
    </header>
  );
}

export default Header;
