import React, { useState, useEffect, useRef } from 'react';

function Home() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const startX = useRef(0);
  const handleRef = useRef(null);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentIndex((currentIndex + 1) % data.length);
    }, 5000);
    return () => clearInterval(intervalId);
  }, [currentIndex, data.length]);

  function handleNext() {
    setCurrentIndex((currentIndex + 1) % data.length);
  }

  function handlePrev() {
    setCurrentIndex((currentIndex - 1 + data.length) % data.length);
  }

  const handleTouchStart = (event) => {
    startX.current = event.touches[0].clientX;
  };

  const handleTouchMove = (event) => {
    const x = event.touches[0].clientX;
    const deltaX = startX.current - x;
    handleRef.current.style.transform = `translateX(${-deltaX}px)`;
  };

  const handleTouchEnd = (event) => {
    handleRef.current.style.transform = 'translateX(0)';
    const x = event.changedTouches[0].clientX;
    const deltaX = startX.current - x;
    if (deltaX > 100) {
      handleNext();
    } else if (deltaX < -100) {
      handlePrev();
    }
  };

  const cards = sectionData.map((item) => (
    <Cards image={item.image} title={item.title} link={item.link} />
  ));

  return (
    <div ref={handleRef} onTouchStart={handleTouchStart} onTouchMove={handleTouchMove} onTouchEnd={handleTouchEnd}>
      <Header
        image={data[currentIndex].image}
        title={data[currentIndex].title}
        subtitle={data[currentIndex].subtitle}
        isWhite={data[currentIndex].isWhite}
        link={data[currentIndex].link}
        id={data[currentIndex].id}
      />
      <Sections />
      <div className="sections__cards">{cards}</div>
    </div>
  );
}

export default Home;
