import React, { useState, useEffect, useRef } from 'react';
import '../App.css';
import Header from '../components/Header';
import sectionData from '../assets/sectionData';
import Sections from '../components/Sections';
import Cards from '../components/Cards';
import data from '../assets/data';

function Home() {
  const [currentIndex, setCurrentIndex] = useState(0);
  const containerRef = useRef(null);

  useEffect(() => {
    const handleSwipe = (event) => {
      const { clientX } = event.touches ? event.touches[0] : event;
      const { left } = containerRef.current.getBoundingClientRect();
      const swipeDirection = clientX - left > 0 ? 1 : -1;
      setCurrentIndex((currentIndex + swipeDirection + data.length) % data.length);
    };

    containerRef.current.addEventListener('touchstart', (event) => {
      event.preventDefault();
      containerRef.current.addEventListener('touchmove', handleSwipe);
    });

    containerRef.current.addEventListener('touchend', () => {
      containerRef.current.removeEventListener('touchmove', handleSwipe);
    });

    containerRef.current.addEventListener('mousedown', (event) => {
      event.preventDefault();
      containerRef.current.addEventListener('mousemove', handleSwipe);
    });

    containerRef.current.addEventListener('mouseup', () => {
      containerRef.current.removeEventListener('mousemove', handleSwipe);
    });

    return () => {
      containerRef.current.removeEventListener('touchstart', handleSwipe);
      containerRef.current.removeEventListener('touchend', handleSwipe);
      containerRef.current.removeEventListener('mousedown', handleSwipe);
      containerRef.current.removeEventListener('mouseup', handleSwipe);
    };
  }, [currentIndex, data.length]);

  const cards = sectionData.map((item) => (
    <Cards image={item.image} title={item.title} link={item.link} />
  ));

  return (
    <div ref={containerRef}>
      <Header
        image={data[currentIndex].image}
        title={data[currentIndex].title}
        subtitle={data[currentIndex].subtitle}
        isWhite={data[currentIndex].isWhite}
        link={data[currentIndex].link}
        id={data[currentIndex].id}
      />
      <Sections />
      <div className="sections__cards">{cards}</div>
    </div>
  );
}

export default Home;
